    <script type="text/javascript" nonce="{{ request()->attributes->get('csp_script_nonce') }}">
        document.addEventListener('click', function(e) {
            const deleteItemBtn = e.target.closest('.delete-item-btn');

            if (deleteItemBtn) {
                e.preventDefault();
                const itemCode = deleteItemBtn.getAttribute('data-item-code');

                Swal.fire({
                    title: 'Are you sure?',
                    text: `Do you want to delete ${itemCode}?`,
                    icon: 'warning',
                    showCancelButton: true,
                    confirmButtonColor: '#d33',
                    cancelButtonColor: '#3085d6',
                    confirmButtonText: 'Yes, delete it!',
                    reverseButtons: true
                }).then(async (result) => {
                    if (result.isConfirmed) {
                        Swal.fire('Deleted!', 'Your file has been deleted.', 'success')
                            .then(() => {
                                deleteItemBtn.closest('tr').remove();
                            });
                    }
                });
            }
        });
        
        $(function() {
            const $itemInput = $('#item_code');
            const $tableRows = $('tbody tr');
            const $searchButton = $('#searchButton');

            function performSearch() {
                const searchText = $itemInput.val().trim();
                $tableRows.each(function() {
                    const itemCode = $(this).find('td:first').text().trim();
                    if (searchText === "" || itemCode.includes(searchText)) {
                        $(this).show();
                    } else {
                        $(this).hide();
                    }
                });
            }

            $searchButton.on('click', function(e) {
                e.preventDefault();
                performSearch();
            });

            $itemInput.on('keypress', function(event) {
                if (event.which === 13) {
                    event.preventDefault();
                    performSearch();
                }
            });

            $itemInput.focus();
            $itemInput.mask('000.00.000');
        });

        // Popover spareLists
        $(function() {
            var popoverTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="popover"]'))
            var popoverList = popoverTriggerList.map(function (popoverTriggerEl) {
                return new bootstrap.Popover(popoverTriggerEl)
            });

            const $itemInput = $('#item_code');
            const $tableRows = $('tbody tr');
            const $searchButton = $('#searchButton');

            function performSearch() {
                const searchText = $itemInput.val().trim();
                $tableRows.each(function() {
                    const itemCode = $(this).find('td:first').text().trim();
                    if (searchText === "" || itemCode.includes(searchText)) {
                        $(this).show();
                    } else {
                        $(this).hide();
                        $(this).find('[data-bs-toggle="popover"]').popover('hide');
                    }
                });
            }
        });
    </script>
